import React from 'react';
import Card from './Card';
import styles from './Album.module.css'; // Import the CSS module

const Album = ({ album }) => {
  const totalPages = Math.ceil(album.cards.length / 8);
  const pages = [];

  for (let i = 0; i < album.cards.length; i += 8) {
    const pageCards = album.cards.slice(i, i + 8);
    const pageNumber = Math.floor(i / 8) + 1;
    
    pages.push(
      <div key={pageNumber} className={styles.albumPage}>
        {pageCards.map((card, index) => (
          <Card key={index} card={card} />
        ))}
        <div className={styles.pageFooter}>Page {pageNumber} of {totalPages}</div>
      </div>
    );
  }

  return (
    <div className={styles.albumContainer}>
      <h2 className={styles.albumName}>{album.name}</h2>
      {pages}
    </div>
  );
};

export default Album;
